package datatypedemo;

public class Query {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=12,m=13;
		System.out.println("Before Swapping: "+ n + ","+ m);
		n=n+m;
		m=n-m;
		n=n-m;
		System.out.println("After Swapping: "+ n + ","+ m);
		
	}
}
